package study1;

public class OperatorTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(85 + 70);
		System.out.println(85 + 80);
		System.out.println(85+80+70);
		System.out.println((85+80+70)/3);
		
		System.out.println(" ");
		
		int eng = 85;
		int kor = 70;
		int math = 80;
		System.out.println("영어+국어" + (eng+kor));
		System.out.println("영어+수학" + (eng+math));
		System.out.println("총합" + (eng+math+kor));
		System.out.println("평균값" + (eng+math+kor)/3);
		
		System.out.println(" ");
		
		String a = "10";
		String b = "20";
		int c = 30;
		System.out.println(a+b+c);
		
		
	}

}
